{*Start Template Logic DO NOT TOUCH *}

{content = filter(content, lambda c: (contains(c.url, 'blog') ? false : true))}
{content = filter(content, lambda c: (contains(c.url, 'recipe') ? false : true))}
{content = filter(content, lambda c: (contains(c.url, '/3298-') ? false : true))}
{content = filter(content, lambda c: (contains(c.url, '-') ? false : true))}

{content = filter(content, lambda x: x.vars.sailthru_active == true)}

{used = []}
{duplicates = []}
{content = filter(content, lambda c: contains(used, c.url)?push("duplicates", c.url):push("used", c.url) || true)}
{content = filter(content, lambda c: contains(used, c.title)?push("duplicates", c.title):push("used", c.title) || true)}
{content = filter(content, lambda c: contains(used, c.description)?push("duplicates", c.description):push("used", c.description) || true)}


{content = personalize({"algorithm":"interest", "content":content, "size":number_of_stories})}

