        td[class=product_description] {font-size: {int(recommended_item_description_size)+1}px !important;line-height: {(int(recommended_item_description_size)+1)+3}px !important;}
        td[class=product_discount] {font-size: 14px !important;line-height: 17px !important;}
        img[class=socialshare_btn] {width:40px !important; height: 40px !important;}
        a[class=cta_btn] {width:130px !important;font-size: 15px !important;line-height: 40px !important;}
