{manual_subject_line = "Did you forget something?"}  {*add text here to override the default subject line; to use the default -- title of first article -- leave this blank*}
{*
SUGGESTED SUBJECT LINES:
Still Interested?
Did you forget something?
Pick back up where you left off
Complete your purchase before this deal ends
Complete your purchase before this item goes out of stock
*}

{manual_preheader = "Pick back up where you left off"} {*add text here to override the default preheader; to use the default -- description of first article -- leave this blank*}

{newsletter_name = "Sailthru Store in Black & White"} {*this is the title of your newsletter*}

{main_algorithm_to_use = "recently_viewed"}

{fallback_algorithm_to_use = "popular"}

{show_button_above_main_image = "off"} {*toggle display of button above main/hero image*}

{show_hero_price = "off"} {*toggle display a price for main/hero item*}

{currency = "$"} {*Add your currency symbol here; "$","","", or other*}

{description_char_limit = 100} {*limit the # of characters in description text; enter 20 or larger number*}

{main_font = "Lucida Sans Unicode, Arial, sans-serif"} {*set a global font for the entire template; see the Code tab for other good font options*}

{* ----- MASTHEAD ----- *}
{masthead_type = "logo_centered"} {* "full_img" or "logo_centered"; or "off" to collapse it*}
{masthead_title = "Sailthru Store in Black & White"} {* title text or "off" *}
{masthead_title_font = main_font}
{masthead_title_color = "#000001"}{*#hex_code *}
{masthead_title_size = "16"}{* number only *}
{masthead_borders = "#EAEAEA"} {* #hex_code or "off" *}
{masthead_borders_height = "3"}
{masthead_padding = "15"}{* number only *}
{masthead_shading = "off"} {* #hex_code or "off" *}

{*MASTHEAD LOGO IMAGES*}
{logo_image_small = "http://email-media.s3.amazonaws.com/Sailthru/default/images/Sailthru_Logo_v_pos_k.png"}
{logo_image_wide = "http://email-media.s3.amazonaws.com/Sailthru/default/images/Sailthru_Logo_v_pos_k.png"}
{logo_url = "http://www.sailthru.com/marketing-blog/?abandond_cart_masthead"}

{* ----- MESSAGE TO CUSTOMER AT TOP ----- *}
{message_switch = "off"}{* "on" or "off" *}
{message_shading = "off"} {* #hex_code or "off" *}
{message_text_font = main_font}
{message_text_color = "#000001"}{* #hex_code *}
{message_text_size = "14"}{* number only *}
{message_text = "Forget something? Come back and place your order with us today!<br><br>Your Sailthru Store in Black & White Crew"}
{message_salutation_switch = "on"}{* "on" or "off" *}
{message_salutation_intro = "Dear"} {*for example: "Dear","Hello","Thank You" or leave blank*}
{firstNameVar = first_name} {*set the text used for the first name user variable*}
{lastNameVar = last_name} {*set the text used for the last name user variable*}
{customer_name_type = "first_and_last"} {*set salutation to use: first_and_last, last_only, or first_only*}
{default_salutation = "Valued Customer"} {*set this is fallback for case when user does not have any name variables*}


{* ----- FOOTER SOCIAL ----- *}
{footersocial_switch = "on"} {*on or off to display*}
{footersocial_shading = "off"} {* #hex_code or "off" *}
{footersocial_borders = "off"} {* #hex_code or "off" *}
{footersocial_borders_height = "5"} {* number only *}
{footersocial_text = "Follow"}
{footersocial_text_color = "#000001"}{* #hex_code *}
{footersocial_btn_iconcolor = "white"} {* "white", "gray" or "black" *}
{footersocial_btn_bgcolor = "#25252B"} {* #hex_code, "default" or "off" *}
{footersocial_btn_rounded = "on"}{* "on" or "off" *}
{footersocial_btn_border = "off"}{* "black", "gray" or "off" *}

{* ----- SET SOCIAL FOOTER LINKS ----- *}
{*to turn off a button, simply remove the link below by setting the var = ""*}
{footersocial_link_fb = "http://www.facebook.com/"}
{footersocial_link_tw = "https://www.twitter.com/"}
{footersocial_link_gp = "https://plus.google.com/"}
{footersocial_link_li = "https://www.linkedin.com/"}
{footersocial_link_inst = "https://www.instagram.com/"}
{footersocial_link_pi = "https://www.pinterest.com/"}
{footersocial_link_yt = "https://www.youtube.com/"}
{footersocial_link_tu = "http://www.tumblr.com/"}

{* ----- FOOTER STYLES ----- *}
{footer_shading = "off"}{* #hex_code or "off" *}
{footer_text_color = "#000001"}{* #hex_code *}
{footer_text_size = "13px"}
{footer_text_font = main_font}

{* -----SET FOOTER CONTENT----- *}
{*company name + address*}
{company_name = "Sample Company, Inc"}
{company_address = "1234 Address Road, Sample City, NY 00000"}

{*----SENT TO TEXT and EMAIL ADDRESS-----*}
{include_sent_to_email = "on"}
{sent_to_text = "This email has been sent to"}

{*-----OPTOUT/PREFERENCE CENTER TEXT AND LINKS-----*}
{subscription_text = "Manage your subscription:"}
{subscription_link1_text = "Manage Preferences"}
{subscription_link1_url = optout_confirm_url} {*default is Sailthru standard optout/preferences page*}
{display_2nd_link = "off"} {*if you need to display a 2nd link here, set this to "on"*}
{separator = "|"} 
{subscription_link2_text = "Preferences"} {*default is Sailthru standard optout/preferences page*}
{subscription_link2_url = optout_confirm_url}

{* ----- TOP CTA SECTION SETTINGS----- *}
{*this is for a  call-to-action button ABOVE the main hero image*}
{subitems_switch = "off"}{* "on" or "off" *}
{subitems_textarea_switch = "on"}{* "on" or "off" *}
{subitems_shading = "off"} {* #hex_code or "off" *}
{subitems_borders = "on"} {* #hex_code or "off" *}
{subitems_title_size = "16"}{* number only *}
{subitems_title_font = main_font}
{subitems_title_color = "#000001"}{* #hex_code *}
{subitems_description_switch = "on"} {* "on" or "off" *}
{subitems_description_size = "13"}{* number only *}
{subitems_description_font = main_font}
{subitems_description_color = "#000001"}{* #hex_code *}
{subitems_price_size = "16"}{* number only *}
{subitems_price_font = main_font}
{subitems_price_color = "#000001"}{* #hex_code *}
{subitems_discount_switch = "off"} {* "on" or "off" *}
{subitems_discount_size = "13"} {* number only *}

{* ----- TOP CTA SECTION CONTENT----- *}
{subitems_text = "Still interested?"}
{subitems_url = "http://www.sailthru.com/marketing-blog/?browse_abandon_top_cta"}
{subitems_description_text = "some additional text for the CTA"}
{subitems_btn_text = "SHOP NOW"}  {*call-to-action for button*}

{* ----- HERO SECTION ----- *}
{hero_switch = "on"}{* "on" or "off" *}
{hero_textarea_switch = "on"}{* "on" or "off" *}
{hero_shading = "off"} {* #hex_code or "off" *}
{hero_borders = "on"} {* #hex_code or "off" *}
{hero_title_size = "16"}{* number only *}
{hero_title_font = main_font}
{hero_title_color = "#000001"}{* #hex_code *}
{hero_description_switch = "on"} {* "on" or "off" *}
{hero_description_size = "13"}{* number only *}
{hero_description_font = main_font}
{hero_description_color = "#000001"}{* #hex_code *}

{*below price vars are not being used in v1, but may be used in later version*}
{hero_price_size = "16"}{* number only *}
{hero_price_font = main_font}
{hero_price_color = "#000001"}{* #hex_code *}
{hero_discount_switch = "off"} {* "on" or "off" *}
{hero_discount_size = "13"} {* number only *}

{* ----- RECOMMENDATIONS MODULE----- *}
{recommended_section_switch = "off"}{* "on" or "off" *}
{recommended_items = "4"}{*"2", "3" or "4";*} {*select the number of products to display in a row*}
{recommended_img_height = "93"}{* "auto" or number *}
{recommended_description_switch = "off"}{* "on" or "off" *}
{recommended_price_switch = "off"}{* "on" or "off" *}
{recommended_discount_switch = "off"}{* "on" or "off" *}
{recommended_cta_switch = "on"}{* "on" or "off" *}
{recommended_title_description_height = "40"}{* number only *}
{recommended_heading_text = "RECOMMENDED FOR YOU..."}
{recommended_heading_shading = "off"} {* #hex_code or "off" *}
{recommended_heading_alignment = "center"} {*"left" or "center"*}
{recommended_heading_text_font = main_font}
{recommended_heading_text_size = "16"} {* number only *}
{recommended_heading_text_color = "#000001"}{* #hex_code *}
{recommended_item_shading = "off"} {* #hex_code or "off" *}
{recommended_item_title_size = "14"} 
{recommended_item_title_font = main_font}
{recommended_item_title_color = "#000001"}{* #hex_code *}
{recommended_item_description_size = "12"}{* number only *}
{recommended_item_description_font = main_font}
{recommended_item_description_color = "#000001"}{* #hex_code *}
{recommended_item_price_size = "14"}{* number only *}
{recommended_item_price_font = main_font}
{recommended_item_price_color = "#000001"}{* #hex_code *}
{recommended_item_discount_size = "12"} {* number only *}
{recommended_cta_text = "SHOP NOW"}

{* ----- CALL-TO-ACTION BUTTONS ----- *}
{cta_btn_width = "120"}
{cta_btn_text = "SHOP NOW"}
{cta_btn_text_color = "#fff"} {* #hex_code *}
{cta_btn_bgcolor = "#D85049"} {* #hex_code or "off" *}
{cta_btn_rounded = "on"}{* "on" or "off" *}
{cta_btn_border = "off"}{* "black", "gray" or "off" *}

{* ----- SPACERS ----- *}
{spacer_height = "10"}
{spacer_height_mobile = "20"}
{spacer_line = "on"} {* "on" or "off" *}
{spacer_line_color = "#EAEAEA"} {* #hex_code *}
{spacer_line_height = "1"}

{* ----- PRODUCT: 1-Item Small ----- *}
{sml_1item_img_width = "163"} {*number only*}
{sml_1item_img_height = ""} {*number only*}
{sml_1item_shading = "off"} {* #hex_code or "off" *}
{sml_1item_borders = "#EAEAEA"} {* #hex_code or "off" *}
{sml_1item_text_valign = "middle"} {* "top" or "middle" *}
{sml_1item_title_size = "15"} {* number only *}
{sml_1item_title_font = "Arial,Helvetica,sans-serif"}
{sml_1item_title_color = "#000001"}{* #hex_code *}
{sml_1item_description_switch = "on"} {* "on" or "off" *}
{sml_1item_description_size = "13"}{* number only *}
{sml_1item_description_font = "Arial,Helvetica,sans-serif"}
{sml_1item_description_color = "#000001"}{* #hex_code *}
{sml_1item_text_size = "15"}{* number only *}
{sml_1item_text_font = "Arial,Helvetica,sans-serif"}
{sml_1item_text_color = "#000001"}{* #hex_code *}

{*-----SUBJECT LINE AND PREHEADER-----*}
{subject_line = manual_subject_line}
{preheader_text = manual_preheader}

{*PLEASE DO NOT CHANGE; customer name selection*}
{if customer_name_type == "first_and_last" && length(firstNameVar) > 1 && length(lastNameVar) > 1}
  {customer_name = firstNameVar + " " + lastNameVar}
{else if customer_name_type == "first_only" && firstNameVar != null }
  {customer_name = firstNameVar}
{else if customer_name_type == "last_only" && lastNameVar != null}
  {customer_name = lastNameVar}
{else}
  {customer_name = default_salutation}  
{/if}

{*PERSONALIZATION ALGORITHM SELECTION*}
{total_content_number = int(recommended_items) + 1}

{session_content = personalize({
 "algorithm" : main_algorithm_to_use,
 "session": session
})}

{if length(session_content) < total_content_number}
  {target_num = total_content_number - length(session_content)}
  {pers_content = personalize({"algorithm":fallback_algorithm_to_use,"size":target_num})}
  {content = session_content + pers_content}
  {combined_data_used = true}
{else}
  {content = session_content}
  {only_session_data_used = true}
{/if}

{hero_url = content[0].url}
{hero_image = content[0].image}
{hero_title = content[0].title}
{hero_description = content[0].description}
{hero_price = content[0].price} 
{hero_btn_text = "SHOP NOW"} {*call-to-action for button*}

{number_of_items = int(recommended_items)}
{content = slice(content,1,number_of_items)}

