{all_content = trending.content + blog.content + recipe.content}
{all_content = dedupe(all_content, "image")}
{all_content = dedupe(all_content, "title")}

{usedcontent = []}

{blog_content = filter(all_content, lambda x: contains(x.tags, 'blog'))}
{blog_content = filter(blog_content, lambda c: (contains(usedcontent, c.title) ? false : push("usedcontent", c.title) || true))}
{blog_content = filter(blog_content, lambda c: length(c.image) > 0)}
{blog_content = personalize({ "algorithm" : "interest", "size": 10, "content" : blog_content})}

{trending_content = all_content}
{trending_content = filter(trending_content, lambda c: length(c.image) > 10)}

{recipe_content = filter(all_content, lambda x: contains(x.tags, 'recipes'))}
{recipe_content = filter(recipe_content, lambda c: length(c.image) > 0)}
{recipe_content = personalize({ "algorithm" : "interest", "size": 10, "content" : recipe_content })}

